from Crypto.Util.number import *
from gmpy2 import *
from secret import flag

BITS = 233

def broken(x, bits):
    return x>>bits

def gen():
    p,q = getPrime(512),getPrime(512)
    n = p*q
    return n, broken(p+q, BITS)

n, hint = gen()
e = 0x10001
print(e)
m = bytes_to_long(flag)
assert m < n
c = pow(m, e, n)

print(n)
print(hint)
